using UnityEngine;
using UnityEngine.AI;

public class DrawLine : MonoBehaviour
{
    public Transform target; // The target object to move towards
    public LineRenderer line; // The line renderer component to draw the line
    public NavMeshAgent agent; // The navigation agent component to calculate the path

    void Start()
    {
        // Set the destination of the agent to the target position
        agent.destination = target.position;
    }

    void Update()
    {
        // Get the current path of the agent
        NavMeshPath path = agent.path;

        // Set the position count of the line renderer to the number of corners in the path
        line.positionCount = path.corners.Length;

        // Set the positions of the line renderer to the corners of the path
        line.SetPositions(path.corners);
    }
}
